mapper.select:
select q.*,l.* from ( select qm.*,'K'||qm.szhh||'-K'||qm.ezhh as ldName,mj.value as fxName,l.lxname from jcpd_qmldb qm left join htgl_ldb ld on ld.lxcode=qm.lxcode and qm.szhh>=ld.szhh and ld.ezhh>qm.szhh left join htgl_lxb l on qm.lxcode=l.lxcode left join htgl_mjlx mj on qm.fx=mj.key where qm.fx=#{xcfx} and qm.bbid=(select qmbbid from jcpd_bbkzb where bbid=#{bbid})
and ld.ldcode=#{ldcode}
and qm.lmlx=#{lmlx}
)q left join (select dcid,lxid,ldid as qmldid,bbid as dcbbid,dcsj,dcry from jcpd_lkdczb where bbid=#{bbid} and lxid=#{lxid} )l on q.ldid=l.qmldid order by q.lxname,q.szhh
