mapper.insert:
insert into XXRW_QSRWD(QSRWD_ID, QSRWD_DJBH, QSRWD_XXLY,
QSRWD_SJLX, QSRWD_SJSJ, QSRWD_SJDD,
QSRWD_SJLD, QSRWD_FX, QSRWD_ZH,QSRWD_LXR,QSRWD_LXDH,QSRWD_LXJBSY,QSRWD_SJSM,QSRWD_TBR,QSRWD_TBSJ,QSRWD_TBDW,QSRWD_TBBM,
QSRWD_BZ,QSRWD_SHZT
)
values (#{qsrwdid,jdbcType=VARCHAR}, #{qsrwddjbh,jdbcType=VARCHAR}, #{qsrwdxxly,jdbcType=VARCHAR},
#{qsrwdsjlx,jdbcType=VARCHAR}, #{qsrwdsjsj,jdbcType=TIMESTAMP}, #{qsrwdsjdd,jdbcType=VARCHAR},
#{qsrwdsjld,jdbcType=VARCHAR}, #{qsrwdfx,jdbcType=VARCHAR}, #{qsrwdzh,jdbcType=VARCHAR}, #{qsrwdlxr,jdbcType=VARCHAR},
#{qsrwdlxdh,jdbcType=VARCHAR}, #{qsrwdlmjbsy,jdbcType=VARCHAR},
#{qsrwdsjsm,jdbcType=VARCHAR}, #{qsrwdstbr,jdbcType=VARCHAR}
, #{qsrwdtbsj,jdbcType=TIMESTAMP}
, #{qsrwdtbdw,jdbcType=VARCHAR}
, #{qsrwdtbbm,jdbcType=VARCHAR}
, #{qsrwdbz,jdbcType=VARCHAR}
, #{qsrwdshzt,jdbcType=VARCHAR}
)

mapper.select:
SELECT T.QSRWD_ID qsrwdid, T.QSRWD_DJBH qsrwddjbh, T.QSRWD_XXLY qsrwdxxly, T.QSRWD_SJLX qsrwdsjlx, T.QSRWD_SJDD qsrwdsjdd, T.QSRWD_SJLD qsrwdsjld, T.QSRWD_FX qsrwdfx, T.QSRWD_ZH qsrwdzh, T.QSRWD_LXR qsrwdlxr, T.QSRWD_LXDH qsrwdlxdh, T.QSRWD_LXJBSY qsrwdlmjbsy, T.QSRWD_SJSM qsrwdsjsm, T.QSRWD_TBR qsrwdstbr, T.QSRWD_TBDW qsrwdtbdw, T.QSRWD_TBBM qsrwdtbbm, T.QSRWD_BZ qsrwdbz, T.QSRWD_SHZT qsrwdshzt, T.QSRWD_SJSJ qsrwdsjsj, T.QSRWD_TBSJ qsrwdtbsj FROM XXRW_QSRWD T
and T.qsrwdsjlx=#{qsrwdsjlx}
and T.qsrwdtbdw=#{qsrwdtbdw}
and T.v=#{qsrwdtbbm}
and T.qsrwdsjsj=#{qsrwdsjsj}
ORDER BY qsrwdsjsj DESC
