mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY A.XCRW_TBRQ DESC) AS RN,A.* FROM RCYH_QDXC A
WHERE 1 = 1
AND A.XCRW_DWMC LIKE #{dwmc}||'%'
AND A.XCRW_DWID = #{dwid}
AND A.XCRW_BMMC LIKE #{bmmc}||'%'
AND A.XCRW_BMID = #{bmid}
AND A.XCRW_TBR LIKE #{tbr}||'%'
AND A.XCRW_XCLX = #{xclx}
AND TO_NUMBER(TO_CHAR(XCRW_KSSJ,'yyyymmdd')) > TO_NUMBER(#{kssj})
AND TO_NUMBER(TO_CHAR(XCRW_JSSJ,'yyyymmdd')) < TO_NUMBER(#{jssj})
) TEMP
RN <= (${pageNum} * ${pageSize}) AND RN>((${pageNum} - 1) * ${pageSize})

SELECT COUNT(*) FROM RCYH_QDXC A
WHERE 1 = 1


    AND XCRW_DWMC LIKE #{dwmc}||'%'



    AND A.XCRW_DWID = #{dwid}



    AND A.XCRW_BMMC LIKE #{bmmc}||'%'



    AND A.XCRW_BMID = #{bmid}



    AND A.XCRW_TBR LIKE #{tbr}||'%'



    AND A.XCRW_XCLX = #{xclx}



    AND TO_NUMBER(TO_CHAR(XCRW_KSSJ,'yyyymmdd'))  > TO_NUMBER(#{kssj})



    AND TO_NUMBER(TO_CHAR(XCRW_JSSJ,'yyyymmdd'))  < TO_NUMBER(#{jssj})

SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY RWMX_TBRQ DESC) AS RN,SHMJ.MEIJUMC AS SHLXNAME,BHMJ.MEIJUMC AS BHLXNAME,SJMJ.MEIJUMC AS SJLXNAME,A.* FROM RCYH_XCSB A LEFT JOIN HTGL_ENUMERATE SHMJ ON A.RWMX_SHLX = SHMJ.MEIJUBM LEFT JOIN HTGL_ENUMERATE BHMJ ON A.RWMX_BHLX = BHMJ.MEIJUBM LEFT JOIN HTGL_ENUMERATE SJMJ ON A.RWMX_SJLX = SJMJ.MEIJUBM WHERE 1=1
AND A.RWMX_DWMC LIKE #{dwmc}||'%'
AND A.RWMX_DWID = #{dwid}
AND A.RWMX_BMMC LIKE #{bmmc}||'%'
AND A.RWMX_BMID = #{bmid}
AND A.RWMX_TBR LIKE #{tbr}||'%'
AND TO_NUMBER(TO_CHAR(XCRW_JSSJ,'yyyymmdd')) > TO_NUMBER(#{stime})
AND TO_NUMBER(TO_CHAR(XCRW_JSSJ,'yyyymmdd')) < TO_NUMBER(#{etime})
AND A.RWMX_SHLX = #{shlx}
AND A.RWMX_BHLX = #{bhlx}
) TEMP
RN <= (${pageNum} * ${pageSize}) AND RN>((${pageNum} - 1) * ${pageSize})

SELECT COUNT(*) FROM RCYH_XCSB A WHERE 1=1


    AND A.RWMX_DWMC LIKE #{dwmc}||'%'



    AND A.RWMX_DWID = #{dwid}



    AND A.RWMX_BMMC LIKE #{bmmc}||'%'



    AND A.RWMX_BMID = #{bmid}



    AND A.RWMX_TBR LIKE #{tbr}||'%'



    AND TO_NUMBER(TO_CHAR(XCRW_JSSJ,'yyyymmdd'))  > TO_NUMBER(#{stime})



    AND TO_NUMBER(TO_CHAR(XCRW_JSSJ,'yyyymmdd'))  < TO_NUMBER(#{etime})



    AND A.RWMX_SHLX = #{shlx}



    AND A.RWMX_BHLX = #{bhlx}

SELECT RWMX_ID,RWMX_DWMC,'巡查方向',RWMX_CD,'单位工程',BHMJ.MEIJUMC AS BHLXNAME,RWMX_BHSL,RWMX_SJSM,RWMX_ZH,RWMX_NOTE
    FROM RCYH_XCSB A LEFT JOIN HTGL_ENUMERATE BHMJ ON A.RWMX_BHLX = BHMJ.MEIJUBM
    WHERE LENGTHB(YHRWD_DJBH||'1') <= 1 AND RWMX_DWMC = #{dw}

SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY YHRWD_RQ DESC) AS RN,A.YHRWD_DJBH, LX.LXNAME,A.YHRWD_LX,A.YHRWD_RQ,A.YHRWD_YD, A.YHRWD_YHDW,A.YHRWD_GYDW,A.YHRWD_RWDSHZT, A.YHRWD_YSZT,SHMJ.MEIJUMC AS SHZT,YSMJ.MEIJUMC AS YSZT FROM RCYH_XXRW_YHRWD A LEFT JOIN HTGL_LXB LX ON A.YHRWD_LX = LX.ID LEFT JOIN HTGL_ENUMERATE SHMJ ON A.YHRWD_RWDSHZT = SHMJ.MEIJUBM LEFT JOIN HTGL_ENUMERATE YSMJ ON A.YHRWD_YSZT = YSMJ.MEIJUBM WHERE 1=1
AND TO_NUMBER(TO_CHAR(YHRWD_RQ,'yyyymmdd')) > TO_NUMBER(#{stime})
AND TO_NUMBER(TO_CHAR(YHRWD_RQ,'yyyymmdd')) < TO_NUMBER(#{etime})
AND A.YHRWD_RWDSHZT = #{yhrwdrwdshzt}
AND A.YHRWD_YSZT = #{yhrwdyszt}
AND TO_NUMBER(TO_CHAR(YHRWD_YD,'yyyymm')) < TO_NUMBER(#{yhrwdyd})
) TEMP
RN <= (${pageNum} * ${pageSize}) AND RN>((${pageNum} - 1) * ${pageSize})

SELECT COUNT(*) FROM RCYH_XXRW_YHRWD A WHERE 1=1


    AND TO_NUMBER(TO_CHAR(YHRWD_RQ,'yyyymmdd'))  > TO_NUMBER(#{stime})



    AND TO_NUMBER(TO_CHAR(YHRWD_RQ,'yyyymmdd'))  < TO_NUMBER(#{etime})



    AND A.YHRWD_RWDSHZT = #{yhrwdrwdshzt}



    AND A.YHRWD_YSZT = #{yhrwdyszt}



    AND TO_NUMBER(TO_CHAR(YHRWD_YD,'yyyymm'))  < TO_NUMBER(#{yhrwdyd})

SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY QSRWD_TBSJ DESC) AS RN,SJLXMJ.MEIJUMC AS SJLXNAME, LX.LXNAME,SHMJ.MEIJUMC AS SHZTNAME,QSMJ.MEIJUMC AS SQZTNAME,A.* FROM RCYH_XXRW_QSRWD A LEFT JOIN HTGL_ENUMERATE SJLXMJ ON A.QSRWD_SJLX = SJLXMJ.MEIJUBM LEFT JOIN HTGL_ENUMERATE SHMJ ON A.QSRWD_SHZT = SHMJ.MEIJUBM LEFT JOIN HTGL_ENUMERATE QSMJ ON A.QSRWD_QSZT = QSMJ.MEIJUBM LEFT JOIN HTGL_LXB LX ON A.QSRWD_SJLD = LX.ID WHERE 1=1
AND TO_NUMBER(TO_CHAR(QSRWD_TBSJ,'yyyymmdd')) > TO_NUMBER(#{stime})
AND TO_NUMBER(TO_CHAR(QSRWD_TBSJ,'yyyymmdd')) < TO_NUMBER(#{etime})
AND A.QSRWD_SJLD = #{qsrwdsjld}
AND A.QSRWD_SJLX = #{qsrwdsjlx}
) TEMP
RN <= (${pageNum} * ${pageSize}) AND RN>((${pageNum} - 1) * ${pageSize})

SELECT COUNT(*) FROM RCYH_XXRW_QSRWD A
WHERE 1=1


    AND TO_NUMBER(TO_CHAR(QSRWD_TBSJ,'yyyymmdd'))  > TO_NUMBER(#{stime})



    AND TO_NUMBER(TO_CHAR(QSRWD_TBSJ,'yyyymmdd'))  < TO_NUMBER(#{etime})



    AND A.QSRWD_SJLD = #{qsrwdsjld}



