mapper.select:
select htbh,htlynr gcnr,fj je,'' yj,'' bz from ZXYH_LYJC t1 left join ZXYH_LYJC_MX t2 on t1.id=t2.lyjcid

    and htbh=#{htbh}


    and to_char(jcrq,'yyyy')=#{nf}


    and to_number(to_char(jcrq,'mm'))=to_number(#{yf})


union all
select htbh,htlynr gcnr,fj je,'' yj,'' bz from ZXYH_JCZBQKB t1 left join ZXYH_JCZBQKB_MX t2 on t1.id=t2.jcid

    and htbh=#{htbh}


    and to_char(jcrq,'yyyy')=#{nf}


    and to_number(to_char(jcrq,'mm'))=to_number(#{yf})

order by htbh
